const Card = ({ fruit, isFlipped, onClick }) => {
    return (
      <div className={`card ${isFlipped ? 'flipped' : ''}`} onClick={onClick}>
        {isFlipped ? <img src={`img/${fruit}.png`} alt={fruit} /> : <img src="../img/abacaxi.jpg" alt="card back" />}
      </div>
    );
  };
  
export default Card;
