import React, { useState, useEffect } from 'react';
import Board from '../Board';
i

const Game = () => {
  const [cards, setCards] = useState([]);
  const [flippedIds, setFlippedIds] = useState([]);
  const [matchedPairs, setMatchedPairs] = useState([]);

  const generateCards = () => {
    const pairs = Array.from({ length: 4 }, (_, index) => index + 1);
    const initialCards = [...pairs, ...pairs].map((value, index) => ({
      id: index,
      value,
      isFlipped: false,
    }));
    return initialCards.sort(() => Math.random() - 0.5);
  };

  useEffect(() => {
    setCards(generateCards());
  }, []);

  const handleCardClick = (clickedId) => {
    if (flippedIds.length < 2 && !flippedIds.includes(clickedId)) {
      const newFlippedIds = [...flippedIds, clickedId];
      setFlippedIds(newFlippedIds);

      if (newFlippedIds.length === 2) {
        const [firstId, secondId] = newFlippedIds;
        if (cards[firstId].value === cards[secondId].value) {
          setMatchedPairs([...matchedPairs, cards[firstId].value]);
        }
        setTimeout(() => setFlippedIds([]), 1000);
      }
    }
  };

  return (
    <div className="game">
      <h1>Memory Game</h1>
      <Board cards={cards} onCardClick={handleCardClick} />
      {matchedPairs.length === 4 && <p>Congratulations! You've matched all pairs!</p>}
    </div>
  );
};

export default Game;
