import React from 'react';
class SeuComponente extends React.Component {
    constructor(props) {
      super(props);
      this.handleClick = this.handleClick.bind(this);
}
handleClick(id) {
    // Lógica de manipulação do clique
    console.log(`Card ${id} clicado`);
}
    Card = ({ id, img, isFlipped, onClick }) => {
    return (
        <div>
            {/* Exemplo de uso do componente Card */}
            <Card id={1} img="./caminho/da/imagem1.jpg" isFlipped={true} onClick={handleClick} />
            <Card id={2} img="./caminho/da/imagem2.jpg" isFlipped={false} onClick={handleClick} />
            {/* ... */}
            </div>
    );
    };}

export default Card;
