import React from 'react';

const Card = ({ id, number, isFlipped, onClick }) => {
  return (
    <div className={`card ${isFlipped ? 'flipped' : ''}`} onClick={() => onClick(id)}>
      {isFlipped ? number : ' '}
    </div>
  );
};

export default Card;
