import React from 'react';

const Card = ({ fruit, isFlipped, onClick }) => {
    return (
      <div className={`card ${isFlipped ? 'flipped' : ''}`} onClick={onClick}>
        {isFlipped ? <img src={`images/${fruit}.png`} alt={fruit} /> : <img src="images/back.png" alt="card back" />}
      </div>
    );
  };
  
  export default Card;
